import React from 'react';
import { SafeAreaView, View, FlatList, StyleSheet, Text, StatusBar, Image, TouchableOpacity } from 'react-native';

const Item = ({ title, image }) => (
  <TouchableOpacity>
    <Text onPress={() => navigation.navegate()}>{platos?.title}</Text>
    <View style={styles.item}>
      <Text style={styles.title}>{title}</Text>
      <Image style={styles.image} source={image}></Image>
    </View>
  </TouchableOpacity>
);

const ListaPlatos = (props) => {

  const {onPress, platos} = props

  return (
    <TouchableOpacity
>
      <FlatList

        data={platos}
        renderItem={({ item }) => <Item title={item.title} image={item.image} />} 
        //agarra el objeto platos y lo separa en cada componente que almacena en item 
        //y lo convierte en un componente item y le envia la info del string 
        // a Item y lo muestra en Item
        keyExtractor={item => item.id}
        
      />
      </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight || 0,
  },
  item: {
    padding: 20,
    marginVertical: 8,
    marginHorizontal: 12,
  },
  title: {
    fontSize: 15,
  },
  image: {
    width: 70,
    height:70,
  },
});

export default ListaPlatos;
