import React from 'react';
import { SafeAreaView, View, FlatList, StyleSheet, Text, StatusBar, Image } from 'react-native';

const Item = ({ title, image }) => (
  <View style={styles.item}>
    <Text style={styles.title}>{title}</Text>
    <Image style={styles.image} source={image}></Image>
    
  </View>
);

const App = (props) => {

  const {onPress, platos} = props

  return (
    <View>
            <View style={styles.header}>
                <Text style={styles.title}>Platos</Text>
                <Text onPress={() => navigation.navigate("Login")}>Mis Platos</Text>
            </View>
            <TextInput
                onChangeText={onChange}
                style={styles.input}
            />
    <SafeAreaView style={styles.container}>
      <FlatList
        data={platos}
        renderItem={({ item }) => <Item title={item.title} image={item.image} />} 
        //agarra el objeto platos y lo separa en cada componente que almacena en item 
        //y lo convierte en un componente item y le envia la info del string 
        // a Item y lo muestra en Item
        keyExtractor={item => item.id}
        
      />
    </SafeAreaView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight || 0,
  },
  item: {
    padding: 20,
    marginVertical: 8,
    marginHorizontal: 12,
  },
  title: {
    fontSize: 15,
  },
  image: {
    width: 13
    
  },
  header: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center"
}, 
input: {
  borderColor: "#ccc",
  borderBottomWidth: 2,
  padding: 2
},
title: {
  fontSize: 22,
},
});

export default App;
