import React from 'react';
import { SafeAreaView, View, FlatList, StyleSheet, Text, StatusBar, Image } from 'react-native';
import SelectDropdown from 'react-native-select-dropdown';
import DropDownPicker from 'react-native-dropdown-picker';

const Item = ({ title, image }) => (
  <View style={styles.item}>
    <Text style={styles.title}>{title}</Text>
    <Image style={styles.image} source={image}></Image>
  </View>
);

const App = (props) => {

  const {onPress, platos} = props

  return (
    <SafeAreaView style={styles.container}>

            <DropDownPicker
            style={styles.DropDownPicker}
            placeholder="Seleccionar Rubros"
              open={open}
              value={value}
              items={Item}
              setOpen={setOpen}
              setValue={setValue}
              setItems={setItems}
              data={platos}
              renderItem={({ item }) => <Item title={item.title} image={item.image} />} 
              //agarra el objeto platos y lo separa en cada componente que almacena en item 
              //y lo convierte en un componente item y le envia la info del string 
              // a Item y lo muestra en Item
              keyExtractor={item => item.id}
              onPress={onPress}
    />

    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight || 0,
  },
  item: {
    padding: 20,
    marginVertical: 8,
    marginHorizontal: 12,
  },
  title: {
    fontSize: 15,
  },
  image: {
    width: 13
    
  },
  DropDownPicker: {
    fontSize: 18,
    marginTop:'5%',
    marginLeft:'10%',
    width: '80%',
    alignItems: 'center',
    borderWidth: 2,
    top: '18%',
    borderRadius: 0,
    backgroundColor:'transparent'
  },


});

export default App;
