import React from 'react';
import { SafeAreaView, View, FlatList, StyleSheet, Text, StatusBar, Image } from 'react-native';

const Item = ({ title, image }) => (
  <View style={styles.item}>
    <Text style={styles.title}>{title}</Text>
    <Image >{image}</Image>
  </View>
);

const App = (props) => {

  const {onPress, platos} = props

  return (
    <SafeAreaView style={styles.container}>
      
      <FlatList
        data={platos}
        renderItem={({ itemSeparadoDelObjeto }) => <Item title={itemSeparadoDelObjeto.title} image={itemSeparadoDelObjeto.image} />} 
        //agarra el objeto platos y lo separa en cada componente que almacena en itemSeparadoDelObjeto
        // y lo convierte en un componente item y lo muestra en la funcion MostrarItem
        keyExtractor={itemSeparadoDelObjeto => itemSeparadoDelObjeto.id}
        onPress={onPress}
      />
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight || 0,
  },
  item: {
    padding: 20,
    marginVertical: 8,
    marginHorizontal: 12,
  },
  title: {
    fontSize: 15,
  },
});

export default App;
