import React from 'react';
import { View, FlatList, StyleSheet, Text, StatusBar, Image } from 'react-native';
import {ActionTypes, useContextState} from '../ContextState'
import BotonAgregar from "../Components/BotonAgregar";
import BotonEliminar from "../Components/BotonEliminar";

const Item = ({ title, image}) => (
   <View>
    <View style={styles.item}>
      <Text style={styles.title}>{title}</Text>
      <Image style={styles.image} source={image}></Image>
    </View>
  </View>
);

const CardPlato = (props) => {

  const {navigation, Detalle} = props
  const { contextState, setContextState} = useContextState();
  let existePlato = contextState.lista.find(plato => plato.id === Detalle.id)

  return (
    <View>
        <Item title={Detalle.title} image={Detalle.image}/>
    {
      existePlato
      ?
      <>
      <BotonEliminar style={{ fontSize: 48}}     
          text="ELIMINAR" 
          onPress={async () =>{
             
          navigation.navigate('Home')    
      }}  
        />
      </>
      :
      <>
        <BotonAgregar style={{ fontSize: 48}}     
          text="AGREGAR" 
          onPress={async () =>{
            setContextState({
              type: ActionTypes.SetMenu,
              value: Detalle.menu,
              });
              setContextState({
              type: ActionTypes.SetMenuPrecio,
              value: Detalle.menu.precio,
              });
              setContextState({
              type: ActionTypes.SetMenuHealthScore,
              value: Detalle.menu.HealthScore,
              });
              setContextState({
              type: ActionTypes.SetMenuLista,
              value: Detalle.menu.lista,
              });
          navigation.navigate('Home')    
      }}  
        />
      </>
    }

  </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight || 0,
  },
  item: {
    padding: 20,
    marginVertical: 8,
    marginHorizontal: 12,
  },
  title: {
    fontSize: 15,
  },
  image: {
    width: 70,
    height:70,
  },
});

export default CardPlato;
