import * as React from "react";
import {useContext} from "react";

export const initialState = {
    loading: true,
        token: "",
        menu: {
            nombre: "",
            imagenn:"",
            precio:0,
            HealthScore:0,
            vegano:false,
            lista:[],
        },
};

export const ActionTypes = {
    SetLoading: "SET_LOADING",
    SetToken: "SET_TOKEN",
    SetMenu: "SET_MENU",
    SetMenuNombre: "SET_MENU_NOMBRE",
    SetMenuImagenn: "SET_MENU_IMAGENN",
    SetMenuPrecio: "SET_MENU_PRECIO",
    SetMenuHealthScore: "SET_MENU_HEALTHSCORE",
    SetMenuVegano: "SET_MENU_VEGANO",
    SetMenuLista: "SET_MENU_LISTA",
};

export const reducer = (state = {}, action) => {
    switch (action.type) {
        case ActionTypes.SetLoading:
        return {
            ...state,
            loading: action.value,
        };
        case ActionTypes.SetToken:
         return {
            ...state,
            token: action.value,
        };
        case ActionTypes.SetMenu:
            return {
               ...state,
               menu: action.value,
           };
           case ActionTypes.SetMenuNombre:
            return {
               ...state,
               nombre: action.value,
           };
           case ActionTypes.SetMenuImagenn:
            return {
               ...state,
               imagenn: action.value,
           };
           case ActionTypes.SetMenuPrecio:
            return {
               ...state,
               precio: action.value,
           };
           case ActionTypes.SetMenuHealthScore:
            return {
               ...state,
               HealthScore: action.value,
           };
           case ActionTypes.SetMenuVegano:
            return {
               ...state,
               vegano: action.value,
           };
           case ActionTypes.SetMenuLista:
            return {
               ...state,
               lista: action.value,
           };
        default:
            return state;
    }
};

export const initialContext = {
    contextState: initialState,
    setContextState: () => {},
};

const Cont = React.createContext(initialContext);

export function ContextProvider({children, initial = initialState}) {
    const [state, dispatch] = React.useReducer(reducer, initial);

    const contextState = state;
    const setContextState = dispatch;

    return <Cont.Provider value={{contextState, setContextState}}>{children}</Cont.Provider>;
}

export const useContextState = () => useContext(Cont);
